import React from "react";
import { useEffect, useState } from "react";
import { Button } from "./Button.js";

let index = 0, 
    correct = "",
    buttons = [], question = "";

const checkAnswer = (answer, data) => {
    if (answer == correct) {
        index++;
        buttons = getButtons(data);
        question = getQuestion(data); 
    }
}

const getAnswers = (data) => {
    let answers = [], 
        correct = data[index].correctAnswer;

    answers.push(correct);
    for (let j=0; j<3; j++) {
        answers.push(data[index].incorrectAnswers[j]);
    }
    answers.sort();

    return answers;
}

const getButtons = (data) => {
    let buttons = [],
        answers = getAnswers(data);

    for (let i=0; i<answers.length; i++) {
        buttons.push(<button className="test" key={i} onClick={(e) => checkAnswer(e, data)}>{answers[i]}</button>);
    }

    return buttons;
}

const getQuestion = (data) => {
    return data[index].question;
}


export const Test = () => {
    const [data, setData] = useState(null);

    useEffect(()=>{
        fetch('https://api.trivia.willfry.co.uk/questions?categories=geography&limit=10')
             .then(res => {
                 return res.json();
             })
             .then(data => {
                 setData(data);
             })
    }, [index]);

    if (data) {
        buttons = getButtons(data);
        question = getQuestion(data);
    }

    return (<div>
                <h1>{question}</h1>
                {buttons}
            </div>
        );
}
