import React from "react";
import { useEffect, useState } from "react";
import { Button } from "./Button.js";

let index = 0;

const getAnswers = (data) => {
    let answers = [];

    answers.push(data[index].correctAnswer);
    for (let j=0; j<3; j++) {
        answers.push(data[0].incorrectAnswers[j]);
    }
    answers.sort();

    return answers;
}

const getButtons = (data) => {
    let buttons = [],
        answers = getAnswers(data);

    for (let i=0; i<answers.length; i++) {
        buttons.push(<Button className="test" id="{i}">{answers[i]}</Button>);
    }

    return buttons;
}

const getQuestion = (data) => {
    return data[index].question;
}


export const Test = () => {
    const [data, setData] = useState(null);

    let buttons = [], question = "";

    useEffect(()=>{
        fetch('https://api.trivia.willfry.co.uk/questions?categories=geography&limit=10')
             .then(res => {
                 return res.json();
             })
             .then(data => {
                 setData(data);
             })
    }, []);

    if (data) {
        buttons = getButtons(data);
        question = getQuestion(data);
    }

    return (<div>
                <h1>{question}</h1>
                {buttons}
            </div>
        );
}
